    private void txtradesferaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtradesferaKeyTyped
       char c=evt.getKeyChar();
            
        
                
          if(Character.isLetter(c)) {
              getToolkit().beep();
              
              evt.consume();
              
              JOptionPane.showMessageDialog(rootPane,"Ingresa Solo Numeros");}
    }//GEN-LAST:event_txtradesferaKeyTyped

